DROP TABLE IF EXISTS `shops`;
CREATE TABLE `shops` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `code` varchar(191) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `name` varchar(191) COLLATE utf8mb4_unicode_ci NOT NULL,
  `name_en` varchar(191) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '' COMMENT '门店英文名称',
  `cover_pic_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '门店正常售卖时的图片',
  `closed_cov_pic_id` int(10) DEFAULT NULL COMMENT '门店不可售卖时的图片',
  `closed_msg_pic_id` int(10) DEFAULT NULL COMMENT '门店不可售卖时的文案图片',
  `address_keyword` varchar(191) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '关键字',
  `outer_id` int(10) unsigned DEFAULT NULL,
  `policy_id` int(11) NOT NULL COMMENT '策略ID',
  `company_id` int(10) NOT NULL DEFAULT '0' COMMENT '公司ID',
  `no` varchar(191) COLLATE utf8mb4_unicode_ci NOT NULL,
  `is_actived` tinyint(1) NOT NULL DEFAULT '1',
  `is_enable` tinyint(4) NOT NULL DEFAULT '0' COMMENT '门店启停1为启用',
  `is_show_official` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否展示在官网',
  `is_open` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '门店状态标签0：敬请期待,1:已经开启门店',
  `is_sync_pos_order` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否同步线下订单',
  `contact_name` varchar(191) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '',
  `country` varchar(100) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '中国' COMMENT '国家名称',
  `country_code` varchar(50) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '国家代码',
  `contact_phone` varchar(191) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '',
  `province` varchar(191) COLLATE utf8mb4_unicode_ci NOT NULL,
  `city` varchar(191) COLLATE utf8mb4_unicode_ci NOT NULL,
  `district` varchar(191) COLLATE utf8mb4_unicode_ci NOT NULL,
  `district_code` varchar(50) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '区号',
  `address` varchar(191) COLLATE utf8mb4_unicode_ci NOT NULL,
  `city_code` varchar(191) COLLATE utf8mb4_unicode_ci NOT NULL,
  `longitude` varchar(191) COLLATE utf8mb4_unicode_ci NOT NULL,
  `gaode_city_code` varchar(10) COLLATE utf8mb4_unicode_ci DEFAULT '' COMMENT '高德地图的city_code',
  `cup_limit` smallint(5) unsigned NOT NULL DEFAULT '10' COMMENT '门店订单杯数限制',
  `latitude` varchar(191) COLLATE utf8mb4_unicode_ci NOT NULL,
  `before_minutes` int(10) unsigned NOT NULL DEFAULT '0',
  `days_of_week` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `time_interval` int(10) unsigned NOT NULL DEFAULT '30',
  `unit_box_seconds` int(10) unsigned NOT NULL DEFAULT '180',
  `unit_box_shares` int(10) unsigned NOT NULL DEFAULT '5',
  `min_charge` int(10) unsigned NOT NULL DEFAULT '40' COMMENT '起送价',
  `open_at` varchar(191) COLLATE utf8mb4_unicode_ci NOT NULL,
  `close_at` varchar(191) COLLATE utf8mb4_unicode_ci NOT NULL,
  `delivery_distance` int(10) unsigned NOT NULL DEFAULT '4800' COMMENT '外卖配送距离',
  `delivery_fee` decimal(8,2) NOT NULL DEFAULT '5.00' COMMENT '外卖配送费',
  `delivery_open_at` varchar(191) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '外卖开始时间',
  `delivery_close_at` varchar(191) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '外卖结束时间',
  `last_operated_at` datetime DEFAULT NULL,
  `takeaway_last_operate_at` datetime DEFAULT NULL COMMENT '外卖启停最后操作时间',
  `support_takeaway` tinyint(1) NOT NULL DEFAULT '0',
  `support_mt_takeaway` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否支持美团外卖',
  `support_sf_takeaway` tinyint(1) NOT NULL DEFAULT '1' COMMENT '是否支持顺丰外卖',
  `takeaway_status` tinyint(3) unsigned NOT NULL DEFAULT '1' COMMENT '外卖状态0关1开',
  `disable_order_type` tinyint(1) DEFAULT '0' COMMENT '0 全部开启；1 禁用自取单；2 禁用预约单；3:全部禁用',
  `scene_code` varchar(50) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '' COMMENT '小程序码',
  `qrcode` varchar(50) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '' COMMENT '小程序二维码',
  `tips` varchar(20) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '门店提示语',
  `is_support_premade` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否支持预制品售卖',
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  `deleted_at` timestamp NULL DEFAULT NULL,
  `license_pic_ids` varchar(100) COLLATE utf8mb4_unicode_ci DEFAULT '' COMMENT '门店执照图片ID',
  PRIMARY KEY (`id`),
  UNIQUE KEY `shops_no_unique` (`no`),
  UNIQUE KEY `shops_code_unique` (`code`),
  KEY `shops_name_index` (`name`),
  KEY `shops_cover_pic_id_index` (`cover_pic_id`),
  KEY `shops_contact_phone_index` (`contact_phone`),
  KEY `shops_contact_name_index` (`contact_name`),
  KEY `shops_city_code_index` (`city_code`),
  KEY `shops_open_at_index` (`open_at`),
  KEY `shops_close_at_index` (`close_at`),
  KEY `shops_outer_id_index` (`outer_id`),
  KEY `shops_is_actived_index` (`is_actived`),
  KEY `shops_last_operated_at_index` (`last_operated_at`),
  KEY `shops_policy_id_index` (`policy_id`),
  KEY `shops_is_sync_pos_order_index` (`is_sync_pos_order`),
  KEY `company_id` (`company_id`)
) ENGINE=InnoDB AUTO_INCREMENT=275 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

